import React, { Component } from 'react';
import './App.css';
import Country from './components/Country';
import Grid from '@mui/material/Grid';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import FormControlLabel from '@mui/material/FormControlLabel';
import HighlightedCode from 'docs/src/modules/components/HighlightedCode';
import RadioGroup from '@mui/material/RadioGroup';
import Radio from '@mui/material/Radio';
import Paper from '@mui/material/Paper';


class App extends Component {

  state = {
    countries: [
      { id: 1, name: 'United States', gold: 2, silver: 3, bronze: 5 },
      { id: 2, name: 'China', gold: 3, silver: 2, bronze: 1 },
      { id: 3, name: 'Germany', gold: 1, silver: 0, bronze: 2 },
    ],
    medals: [
      { id: 1, name: 'gold' },
      { id: 2, name: 'silver' },
      { id: 3, name: 'bronze' },
    ]
  }

  addMedalCount = (countryId, medalName) => {
    const countries = [ ...this.state.countries ];
    const idx = countries.findIndex(c => c.id === countryId);
    countries[idx][medalName] += 1;
    this.setState({ countries: countries });
  }
  decreaseMedalCount = (countryId, medalName) => {
    const countries = [ ...this.state.countries ];
    const idx = countries.findIndex(c => c.id === countryId);

    if( countries[idx][medalName] > 0 ) {
      countries[idx][medalName] -= 1;
      this.setState({ countries: countries });
    }

  }
  AllMedalsTotal() {
    let sum = 0;
    this.state.medals.forEach(medal => { sum += this.state.countries.reduce((a, b) => a + b[medal.name], 0); });
    return sum;
  }

  render() { 
    return ( 


      <div id="centerContent">
      <div className="App">
        <header className="App-header">
        </header> 

        <Grid sx={{ flexGrow: 1 }} container spacing={2}>
      <Grid item xs={12}>
        <Grid container justifyContent="center" spacing={spacing}>
          {[0, 1, 2].map((value) => (
            <Grid key={value} item>
              <Paper sx={{ height: 140, width: 100 }} />
            </Grid>
          ))}
        </Grid>
      </Grid>
      </Grid>


        <div>
            { this.AllMedalsTotal() }
          </div>
          <Grid container spacing={12}>

            { this.state.countries.map(country => 
            <Country 
              key={ country.id } 
              country={ country }
              medals={ this.state.medals }
              addMedalCount={ this.addMedalCount } 
              decreaseMedalCount={ this.decreaseMedalCount }
            />
            )}
        </Grid>
      </div>
      </div>
     );
  }
}

export default App;
