import React, { Component } from 'react';
import './App.css';
import Country from './components/Country';


class App extends Component {

  state = {
    countries: [
      { id: 1, name: 'United States', gold: 2, silver: 3, bronze: 5 },
      { id: 2, name: 'China', gold: 3, silver: 2, bronze: 1 },
      { id: 3, name: 'Germany', gold: 1, silver: 0, bronze: 2 },
    ],
    medals: [
      { id: 1, name: 'gold' },
      { id: 2, name: 'silver' },
      { id: 3, name: 'bronze' },
    ]
  }

  addMedalCount = (countryId, medalName) => {
    const countries = [ ...this.state.countries ];
    const idx = countries.findIndex(c => c.id === countryId);
    countries[idx][medalName] += 1;
    this.setState({ countries: countries });
  }
  decreaseMedalCount = (countryId, medalName) => {
    const countries = [ ...this.state.countries ];
    const idx = countries.findIndex(c => c.id === countryId);
    countries[idx][medalName] -= 1;
    this.setState({ countries: countries });
  }
  AllMedalsTotal() {
    let sum = 0;
    this.state.medals.forEach(medal => { sum += this.state.countries.reduce((a, b) => a + b[medal.name], 0); });
    return sum;
  }

  render() { 
    return ( 
      <div id="centerContent">
      <div className="App">
        <header className="App-header">
        </header> 

        <div>
            { this.AllMedalsTotal() }
          </div>

        { this.state.countries.map(country => 

          <Country 
            key={ country.id } 
            country={ country }
            medals={ this.state.medals }
            addMedalCount={ this.addMedalCount } 
            decreaseMedalCount={ this.decreaseMedalCount }
            />

            )}
      </div>
      </div>
     );
  }
}

export default App;
