import React, { Component } from 'react';

import Button from '@mui/material/Button';

class Medal extends Component {
  state = {  }
  render() { 
    const { medal, country, addMedalCount, decreaseMedalCount } = this.props;
    return (
      <div className="medals">

        { medal.name } medals:
        <br></br>
         <Button variant="outlined" style={{maxWidth: '25px', maxHeight: '25px', minWidth: '25px', minHeight: '25px'}}
          onClick={ () => decreaseMedalCount(country.id, medal.name) 
        }>
          -
        </Button>
        { country[medal.name] }

        <Button variant="outlined" style={{maxWidth: '25px', maxHeight: '25px', minWidth: '25px', minHeight: '25px'}} 
         onClick={ () => addMedalCount(country.id, medal.name) 
         }>
           +
         </Button>

      </div>
    );
  }
}

export default Medal;
