import React, { Component } from 'react';

class Medal extends Component {
  state = {  }
  render() { 
    const { medal, country, addMedalCount, decreaseMedalCount } = this.props;
    return (
      <div className="medals">
        { medal.name } Medals: { country[medal.name] }
        <button onClick={ () => addMedalCount(country.id, medal.name) }>+</button>
        <button disabled={ country[medal.name] === 0 } onClick={ () => decreaseMedalCount(country.id, medal.name) }>-</button>
      </div>
    );
  }
}

export default Medal;
