import React, { Component } from 'react';
import './App.css';
import Country from './components/Country';


class App extends Component {

  state = {
    countries: [
      { id: 1, name: 'United States', goldMedalCount: 2, silverMedalCount: 3, bronzeMedalCount: 5 },
      { id: 2, name: 'China', goldMedalCount: 3, silverMedalCount: 2, bronzeMedalCount: 1 },
      { id: 3, name: 'Germany', goldMedalCount: 1, silverMedalCount: 0, bronzeMedalCount: 2 },
    ],
    medals: [
      { id: 1, name: 'gold' },
      { id: 2, name: 'silver' },
      { id: 3, name: 'bronze' },
    ]
  }

  handleIncrement = (countryId, medalName) => {
    const countries = [ ...this.state.countries ];
    const idx = countries.findIndex(c => c.id === countryId);
    countries[idx][medalName] += 1;
    this.setState({ countries: countries });
  }
  handleDecrement = (countryId, medalName) => {
    const countries = [ ...this.state.countries ];
    const idx = countries.findIndex(c => c.id === countryId);
    countries[idx][medalName] -= 1;
    this.setState({ countries: countries });
  }
  getAllMedalsTotal() {
    let sum = 0;
    this.state.medals.forEach(medal => { sum += this.state.countries.reduce((a, b) => a + b[medal.name], 0); });
    return sum;
  }

  render() { 
    return ( 
      <div id="centerContent">
      <div className="App">
        <header className="App-header">
        </header> 

        { this.state.countries.map(country => 

          <Country 
            key={ country.id } 
            country={ country }
            medals={ this.state.medals }
            onIncrement={ this.handleIncrement } 
            onDecrement={ this.handleDecrement }
            />

            )}
      </div>
      </div>
     );
  }
}

export default App;
