import React, { Component } from 'react';
import './App.css';
import Country from './components/Country';


class App extends Component {

  state = {
    countries: [
      { id: 1, name: 'United States', goldMedalCount: 2, silverMedalCoun: 3, bronzeMedalCount: 5 },
      { id: 2, name: 'China', goldMedalCount: 3, silverMedalCoun: 2, bronzeMedalCount: 1 },
      { id: 3, name: 'Germany', goldMedalCount: 1, silverMedalCoun: 0, bronzeMedalCount: 2 },
    ]
  }

  render() { 
    return ( 
      <div id="centerContent">
      <div className="App">
        <header className="App-header">
        </header> 

        { this.state.countries.map(country => 

          <Country 
            key={ country.id } 
            country={ country }
            />

            )}
      </div>
      </div>
     );
  }
}

export default App;
