import React, { Component } from 'react';

import ButtonGroup from '@mui/material/ButtonGroup';
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';

class Country extends Component {

    addToCounter = () => {
        this.setState({ goldMedalCount: (this.props.goldMedalCount + 1) })
    }

    removeFromCounter = () => {
        if(this.props.goldMedalCount > 0) {
            this.setState({ goldMedalCount: (this.props.goldMedalCount - 1) })
        }
    }

    render() { 
        const { name } = this.props;

        return (
                    
        // <div style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '100vh'}}>
        <div>   
            <Card style={{padding:'5em'}} sx={{ maxWidth: 500 }}> 
            <CardContent>
            
                <div className="titleFont">
                    { name.name }
                    <hr/>
                </div>

                <div>

                    <div className="smallTextFont">
                        <EmojiEventsIcon/> &nbsp; Gold Medals: &nbsp; <EmojiEventsIcon/>
                    </div>

                    <div className="numberFont">
                        { name.goldMedalCount }
                    </div>
                </div>


                <ButtonGroup style={{marginTop:'.5em'}}>
                <Button
                    color="secondary"
                    aria-label="reduce"
                    onClick={() => {
                        this.removeFromCounter()
                    }}>
                    <RemoveIcon fontSize="small" />
                </Button>

                <Button
                    color="secondary"
                    aria-label="increase"
                    onClick={() => {
                        this.addToCounter()
                    }}>

                    <AddIcon fontSize="small" />
                </Button>
                </ButtonGroup>
            </CardContent>
            
            </Card>   
        </div>           
    );
  }
}

export default Country
