import React, { Component } from 'react';

import Button from '@mui/material/Button';

class Medal extends Component {
  state = {  }
  render() { 
    const { medal, country, addMedalCount, decreaseMedalCount } = this.props;
    return (
      <div className="medals">

        { medal.name } medals:

        <Button variant="outlined" onClick={ () => addMedalCount(country.id, medal.name) }>+</Button>

        { country[medal.name] }

        <Button variant="outlined" disabled={ country[medal.name] === 0 } onClick={ () => decreaseMedalCount(country.id, medal.name) }>-</Button>

      </div>
    );
  }
}

export default Medal;
