import React, { Component } from 'react';

import Button from '@mui/material/Button';

class Medal extends Component {
  state = {  }
  render() { 
    const { medal, country, addMedalCount, decreaseMedalCount } = this.props;
    return (
      <div className="medals">

        { medal.name } medals:

        <Button variant="outlined" style={{maxWidth: '30px', maxHeight: '30px', minWidth: '30px', minHeight: '30px'}} 
         onClick={ () => addMedalCount(country.id, medal.name) 
         }>
           +
         </Button>

        { country[medal.name] }

        <Button variant="outlined" style={{maxWidth: '20px', maxHeight: '20px', minWidth: '20px', minHeight: '20px'}}
          disabled={ country[medal.name] === 0 } 
          onClick={ () => decreaseMedalCount(country.id, medal.name) 
        }>
          -
        </Button>

      </div>
    );
  }
}

export default Medal;
